public static Object javaSerializeReadPojo(byte [] bytes) {
  try {
    final ObjectInputStream ois = new ObjectInputStream(new ByteArrayInputStream(bytes));
    Object o = ois.readObject();
    return o;
  } catch (IOException e) {
    String className = nameOfClass(bytes);
