    MessageDigest digest;
    try {
        digest = MessageDigest.getInstance("SHA1");
    } catch (NoSuchAlgorithmException e) {
        throw new RuntimeException(e);
    }
    digest.update(bytes, 0, bytes.length);
    byte[] sha1sum = digest.digest();
    String display = "";
    for(byte b : sha1sum)
        display += toHex(b);
    return display;

